function getFile(type){
    var url="";
    if(type==1){
        url="/dreamCase/retrieveDreamCaseList";
    }else if(type==2){
        url="/dreamFile/retrieveDreamFileList";
    }
    $.ajax({
        url: baseUrl + url,
        type : "post",
        dataType: "json",
        success: function (result) {
            if (result.success == 1) {
                var content = '<h1>下载</h1>';
                var data = result.data.content;
                var url ="";
                for(var i=0;i<data.length;i++){
                    url = data[i].url;
                    if(url && url.substring(0,4)!="http"){
                        url =  "http://"+url;
                    }
                    content += '<div class="file"><a href="'+url+'">'+data[i].title+'</a></div>';
                }
                $("#my_container").append(content);

            } else {
                alert(result.message);
            }
        }
    })
}